mapper.delete:
delete from volun_article_love
where volun_id = #{volunId}
  and article_id = #{articleId}

mapper.select:
with temp as( select d.nickname,dkd.kid_id,dkd.thing_name,dkd.time from donor_kid_thing as dkd,donor as d where dkd.donor_id = d.id and dkd.kid_id in
#{id}
) select * from temp where time = (SELECT MAX(time) FROM temp)

with temp as( select d.nickname,dkd.kid_id,dkd.amount,dkd.time from donor_kid_donation as dkd,donor as d where dkd.donor_id = d.id and dkd.kid_id in
#{id}
) select * from temp where time = (SELECT MAX(time) FROM temp)

SELECT vm.mission_id, COALESCE(count(km.reply_id), 0) as count
FROM volun_to_mission AS vm
LEFT JOIN kid_to_mission AS km ON vm.mission_id = km.mission_id and km.reply_id IS NOT NULL
WHERE vm.volun_id = #{volunId}
group by vm.mission_id;

select km.mission_id, count(kr.id) as count from kid_to_mission as km,kid_reply as kr where km.reply_id = kr.id and km.mission_id in
#{id}
and kr.score is not null group by km.mission_id

select count(*)
from volun_article_love as val
where val.volun_id = #{volunId}
  and val.article_id = #{articleId}

select count(*)
from volun_article_love as val
  where val.article_id = #{articleId}
